sp_widget.client_script:
function($rootScope,$scope) {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}
}

sp_widget.script:
(function () {
  //Cria um aray para alocar as notas
  data.notes = [];  //Array Vazio
  var noteGR = new GlideRecord('x_snc_createnotes_note'); //Glide record para as notas (Obtem as notas)
  noteGR.addQuery('user', gs.getUser().getID());  //Adiciona requisição do usuário
  noteGR.orderByDesc('sys_created_on'); //Adiciona requisiçã da data de criação
  noteGR.query(); //Realiza a requisição 
  while (noteGR.next()) { //Loop para realizar a leitura do array
var noteObj = {};
    /* $sp é um método auxiliar para acesso ao service portal. Auxiliar para exibição de valores*/
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		//Pega os primeiros 20 caracteres da descrição
		noteObj.note = noteGR.getValue('note').slice(0, 20);
		//Adiciona o valor obtido ao array de dados
    data.notes.push(noteObj);
  }
})();
